{
  "startUrls": [
    { "url": "{{13.norm_url}}" }
  ],

  "linkSelector": "a[href]",

  "includeUrlGlobs": [
    "*/uber-uns*",
    "*/ueber-uns*",
    "*/about*",
    "*/unternehmen*",
    "*/karriere*",
    "*/jobs*",
    "*/benefits*"
  ],

  "crawlerType": "playwright:adaptive",
  "maxCrawlPages": 10,
  "maxCrawlDepth": 3,

  "proxyConfiguration": { "useApifyProxy": true },

  "pageFunction": "async function pageFunction(context) {
  const { request, page, log } = context;
  // Störelemente entfernen
  const toRemove = ['nav','footer','script','style','noscript','svg','img[src^="data:"]'];
  try {
    await page.evaluate((sel) => {
      sel.forEach(s => { document.querySelectorAll(s).forEach(el => el.remove()); });
    }, toRemove);
  } catch (e) {
    log.debug('Cleanup skipped: ' + e.message);
  }

  // Lesbaren Text holen
  const text = await page.evaluate(() => (document.body.innerText || '')
    .replace(/\s+/g, ' ')  // Whitespace glätten
    .trim());

  // Alle Links auslesen
  const allLinks = await page.$$eval('a[href]', as => as.map(a => a.href).filter(Boolean));

  // Social Links filtern
  const socialRe = /(linkedin\.com\/company|facebook\.com|instagram\.com|twitter\.com|x\.com|youtube\.com|kununu\.com|xing\.com|tiktok\.com)/i;
  const socialLinks = Array.from(new Set(allLinks.filter(h => socialRe.test(h))));

  return {
    url: request.url,
    text,
    socialLinks
  };
}",

  "removeElementsCssSelector": "nav, footer, script, style, noscript, svg, img[src^='data:']"
}
